INSERT INTO MASTERES(NOMBRE,DESCRIPCION,CODIGOPLAN,PRECIOAPROXIMADO,PRESENCIALIDAD,DOBLETITULACION,GHOMOLOGADO,GRADO,IDIOMAPRINCIPAL,IDIOMASECUNDARIO,LPROGPRINCIPAL,LPROGSECUNDARIO,CCOORDINACION) VALUES('Máster en Ingeniería de Montes','El Máster en Ingeniería de Montes está disponible.\nEl Máster en Ingeniería de Montes proporciona a los estudiantes formación científica, tecnológica y socio-económica, y preparación para el ejercicio de la profesión de Ingeniero de Montes. El título habilita para el ejercicio de una profesión regulada (Ingeniero Superior de Montes).\nPuedes encontrar más información en:\n https://www.uva.es/export/sites/uva/2.docencia/2.02.mastersoficiales/2.02.01.ofertaeducativa/detalle/Master-en-Ingenieria-de-Montes/\n¿Qué más necesitas?',428,2800,'Presencial',False,True,'Ingeniería Forestal','Español','Inglés','R','','master.ing.montes@uva.es');
INSERT INTO MASTERES(NOMBRE,DESCRIPCION,CODIGOPLAN,PRECIOAPROXIMADO,PRESENCIALIDAD,DOBLETITULACION,GHOMOLOGADO,GRADO,IDIOMAPRINCIPAL,IDIOMASECUNDARIO,LPROGPRINCIPAL,LPROGSECUNDARIO,CCOORDINACION) VALUES('Máster en Dataforest','El Máster en Ingeniería de Gestión Forestal Basada en técnicas de Big Data (Dataforest) está disponible.\nEl Máster en Ingeniería de Gestión Forestal Basada en técnicas de Big Data (Dataforest) proporciona a los estudiantes los conocimientos y herramientas para llevar a cabo la gestión de recursos naturales aplicando sólidas metodologías científicas y de tratamiento de datos para su análisis y solución.\nEncontrarás información en:\n https://www.uva.es/export/sites/uva/2.docencia/2.02.mastersoficiales/2.02.01.ofertaeducativa/detalle/Master-en-Gestion-Forestal-basada-en-Ciencia-de-Datos/\n¿Qué más necesitas?',572,1940,'Presencial',False,False,null,'Español','Inglés','Python','R, MySQL, SAS','master.gestion.forestal@uva.es');
INSERT INTO MASTERES(NOMBRE,DESCRIPCION,CODIGOPLAN,PRECIOAPROXIMADO,PRESENCIALIDAD,DOBLETITULACION,GHOMOLOGADO,GRADO,IDIOMAPRINCIPAL,IDIOMASECUNDARIO,LPROGPRINCIPAL,LPROGSECUNDARIO,CCOORDINACION) VALUES('Máster Doble en Montes y Dataforest','El Máster Doble en Ingeniería de Montes y Dataforest está disponible.\nl Máster Doble en Ingeniería de Montes y AETPF-FEN surge como una oportunidad de internacionalizar los conocimientos adquiridos en el máster de Ingeniería de Montes con una estancia en el segundo curso del máster en Nancy(Francia), permitiendo hacer prácticas en instituciones internacionales.\nPuedes encontrar más información en:\n http://sostenible.palencia.uva.es/sites/default/files/formacion/memoria_doble_mastermontes-dataforest_aprobado.pdf,\n¿Qué más necesitas?',603,3000,'Presencial',True,True,'Ingeniería Forestal','Español','Inglés','Python','R, MySQL, SAS','pec.ing.montes.gestion.forestal@uva.es');
INSERT INTO MASTERES(NOMBRE,DESCRIPCION,CODIGOPLAN,PRECIOAPROXIMADO,PRESENCIALIDAD,DOBLETITULACION,GHOMOLOGADO,GRADO,IDIOMAPRINCIPAL,IDIOMASECUNDARIO,LPROGPRINCIPAL,LPROGSECUNDARIO,CCOORDINACION) VALUES('Máster Doble en Montes y AETPF-FEN','El Máster Doble en Ingeniería de Montes y AETPF-FEN surge como una oportunidad de internacionalizar los conocimientos adquiridos en el máster de Ingeniería de Montes con una estancia en el segundo curso del máster en Nancy(Francia), permitiendo hacer prácticas en instituciones internacionales.',428,3700,'Presencial',True,True,'Ingeniería Forestal','Español','Inglés','R','','master.ing.montes@uva.es');
